DROP DATABASE IF EXISTS pokemon_db;
CREATE DATABASE pokemon_db;
USE pokemon_db;

CREATE TABLE pokemonDataset (abilities VARCHAR(100) NOT NULL, against_bug VARCHAR(100) NOT NULL, against_dark INT NOT NULL, against_dragon INT NOT NULL, against_electric INT NOT NULL, against_fairy INT NOT NULL, against_fight INT NOT NULL, against_fire INT NOT NULL, against_flying INT NOT NULL, against_ghost INT NOT NULL, against_grass INT NOT NULL, against_ground INT NOT NULL, against_ice INT NOT NULL, against_normal INT NOT NULL, against_poison INT NOT NULL, against_psychic INT NOT NULL, against_rock INT NOT NULL, against_steel INT NOT NULL, against_water INT NOT NULL, attack INT NOT NULL, base_egg_steps INT NOT NULL, base_happiness INT NOT NULL, base_total INT NOT NULL, capture_rate INT NOT NULL, classfication INT NOT NULL, defense INT NOT NULL, experience_growth VARCHAR(20), height_m INT NOT NULL, hp INT NOT NULL, japanese_name VARCHAR(50) NOT NULL, name VARCHAR(50), percentage_male INT NOT NULL, pokedex_number INT NOT NULL, sp_attack INT NOT NULL, sp_defense INT NOT NULL, speed INT NOT NULL, type1 INT NOT NULL, type2 VARCHAR(20) NOT NULL, weight_kg INT NOT NULL, generation INT NOT NULL, is_legendary INT NOT NULL, PRIMARY KEY (name));
